mapper.select:
select id
from member
where email = #{email}

select pwd
from member
where id = #{id} and name = #{name} and email = #{email}

mapper.update:
update member
set
pwd = #{pwd}
where id = #{id}

update member
set
name = #{name}
where id = #{id}

update member
set
email = #{email}
where id = #{id}
